INSERT INTO `news` (`id`, `title`, `id_catenews`, `content_news`, `date_created`, `image_news`, `quote`) VALUES
(7, 'Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này', 2, '<p>Dạo khắp S&agrave;i G&ograve;n mới ph&aacute;t hiện c&oacute; nhiều những m&oacute;n l&agrave;m từ c&aacute;c loại thảo mộc, thảo dược phổ biến v&agrave; quen thuộc như la h&aacute;n quả, thục địa, cam thảo... kh&ocirc;ng chỉ ngon miệng, hấp dẫn m&agrave; c&ograve;n tốt cho sức khoẻ đấy.&nbsp;Sau những bữa ăn, nếu c&oacute; &yacute; định ăn nhẹ th&igrave; h&atilde;y nghĩ đến c&aacute;c m&oacute;n từ thảo mộc sau đ&acirc;y nh&eacute;!</p>\r\n\r\n<h3>Ch&egrave; thảo mộc</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/fdb-1553841684369132585399.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/3/29/fdb-1553841684369132585399.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 1.\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/fds-1553841684356809772875.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/3/29/fds-1553841684356809772875.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 1.\" /></a></p>\r\n\r\n<p>Ch&egrave; thảo mộc Đ&agrave;i Loan c&oacute; thể n&oacute;i l&agrave; m&oacute;n ăn vừa &quot;vui miệng&quot; vừa &quot;vui mắt&quot; lại c&ograve;n gi&uacute;p m&aacute;t người. Vui miệng l&agrave; v&igrave; c&oacute; rất nhiều loại topping kh&aacute;c nhau như cao quy linh, thạch thảo mộc, khoai dẻo hay tr&acirc;n ch&acirc;u... vui mắt l&agrave; v&igrave; c&aacute;c hạt topping n&agrave;y c&oacute; nhiều m&agrave;u sắc tr&ocirc;ng rất bắt mắt. Th&ocirc;ng thường, ch&egrave; thảo mộc Đ&agrave;i Loan c&oacute; ba th&agrave;nh phần ch&iacute;nh đ&oacute; l&agrave; đ&aacute; b&agrave;o thảo mộc (nước thảo mộc nấu từ b&iacute; đao, cam thảo, thục địa, vv...), khoai dẻo v&agrave; thạch, cao l&agrave;m từ thảo mộc. Song, ch&egrave; thảo mộc hiện đại đa dạng hơn rất nhiều với nhiều topping kh&aacute;c nhau cho bạn tha hồ chọn lựa.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/thanh-n-phan-15538416954801355364855.jpg\" target=\"_blank\" title=\"Nguồn ảnh: Thanh N.Phan.\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 2.\" id=\"img_b3a450a0-51ed-11e9-92a2-8d0f4958e9e2\" src=\"https://kenh14cdn.com/2019/3/29/thanh-n-phan-15538416954801355364855.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 2.\" /></a></p>\r\n\r\n<p>Nguồn ảnh: Thanh N.Phan.</p>\r\n\r\n<h3>Thạch thảo mộc mật ong</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/ngo-ai-kim-1553842240450649213882.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/3/29/ngo-ai-kim-1553842240450649213882.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 3.\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/nhung-pudding-1553842240452179991219.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/3/29/nhung-pudding-1553842240452179991219.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 3.\" /></a></p>\r\n\r\n<p>Nguồn ảnh: nhung, Ng&ocirc; &Aacute;i Kim.</p>\r\n\r\n<p>Pudding thảo mộc vừa mềm mịn, vừa thơm, sau khi để ngăn m&aacute;t một thời gian v&agrave; lấy ra sẽ rất dễ ăn. Thạch thảo mộc mật ong được l&agrave;m từ nhiều loại thảo mộc tốt cho sức khoẻ v&agrave; lấy vị ngọt từ mật ong thay v&igrave; đường. Ở S&agrave;i G&ograve;n, bạn c&oacute; thể t&igrave;m thấy một số chỗ b&aacute;n thạch thảo mộc uống k&egrave;m với sữa đậu n&agrave;nh. Nếu đ&atilde; ngấy tr&agrave; sữa nhưng vẫn muốn uống c&aacute;i g&igrave; đ&oacute; m&aacute;t m&aacute;t v&agrave; c&oacute; thạch, c&oacute; sữa beo b&eacute;o th&igrave; c&oacute; thể thử m&oacute;n sữa đậu n&agrave;nh thạch thảo mộc.&nbsp;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/screen-shot-2019-03-29-at-14821-pm-1553842282163745007017.png\" target=\"_blank\" title=\"Nguồn ảnh: Bùi Lan Thảo.\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 4.\" id=\"img_12e34a70-51ef-11e9-9649-01a1fa2ce936\" src=\"https://kenh14cdn.com/2019/3/29/screen-shot-2019-03-29-at-14821-pm-1553842282163745007017.png\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 4.\" /></a></p>\r\n\r\n<p>Nguồn ảnh: B&ugrave;i Lan Thảo.</p>\r\n\r\n<h3>Trứng g&agrave; thảo mộc</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/5-15538399864331816817849.jpg\" target=\"_blank\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 5.\" id=\"img_d8c3a100-51e9-11e9-bb55-f918f349437a\" src=\"https://kenh14cdn.com/2019/3/29/5-15538399864331816817849.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 5.\" /></a></p>\r\n\r\n<p>Ch&egrave; trứng g&agrave; tr&agrave; thảo mộc l&agrave; một trong những m&oacute;n ch&egrave; độc đ&aacute;o của người Hoa. Những quả trứng c&oacute; m&agrave;u n&acirc;u sẫm do được ng&acirc;m trong nước ch&egrave; c&ugrave;ng c&aacute;c vị thảo mộc c&oacute; hương thơm kh&oacute; tả. Ai kh&ocirc;ng quen c&oacute; thể thấy hơi lạ, nhưng những người th&iacute;ch vị thảo mộc th&igrave; sẽ cảm thấy m&ugrave;i n&agrave;y rất dễ chịu. Phần l&ograve;ng trắng c&oacute; vị b&eacute;o đặc trưng của trứng, pha lẫn ch&uacute;t đăng đắng từ vị thảo mộc, quyện c&ugrave;ng l&ograve;ng đỏ thơm b&ugrave;i tạo n&ecirc;n m&oacute;n ngon h&agrave;i ho&agrave;. Bạn c&oacute; thể chọn ăn n&oacute;ng hoặc lạnh, nhưng nếu c&oacute; thể th&igrave; h&atilde;y thử phi&ecirc;n bản n&oacute;ng, bởi v&igrave; nhiệt độ sẽ l&agrave;m dậy m&ugrave;i hơn, r&otilde; vị thảo mộc hơn khi ăn lạnh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/1-1553841081682821449383.png\" target=\"_blank\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 6.\" id=\"img_47e9d7f0-51ec-11e9-9055-ed390aa66c3c\" src=\"https://kenh14cdn.com/2019/3/29/1-1553841081682821449383.png\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 6.\" /></a></p>\r\n\r\n<h3>Nước s&acirc;m</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/8-1553840869061949305304.jpg\" target=\"_blank\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 7.\" id=\"img_c6cd79b0-51eb-11e9-aafb-057b46320ac6\" src=\"https://kenh14cdn.com/2019/3/29/8-1553840869061949305304.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 7.\" /></a></p>\r\n\r\n<p>Ở S&agrave;i G&ograve;n, nước s&acirc;m ph&aacute;t triển đến mức c&oacute; thể xem như một nh&aacute;nh &quot;văn ho&aacute; nước s&acirc;m&quot; nho nhỏ. Do trời n&oacute;ng quanh năm m&agrave; thức uống đơn giản n&agrave;y rất được người d&acirc;n nơi đ&acirc;y ưa chuộng. Nước s&acirc;m được nấu từ c&aacute;c loại thảo mộc, thảo dược rất dễ t&igrave;m. C&oacute; hai loại nước s&acirc;m thường thấy l&agrave; nước s&acirc;m nhạt v&agrave; nước s&acirc;m vị đắng (c&oacute; m&agrave;u đậm hơn). Nước s&acirc;m đắng c&oacute; hơi kh&oacute; uống hơn một ch&uacute;t, nhưng nếu uống quen th&igrave; rất c&oacute; lợi cho sức khoẻ do c&oacute; nhiều loại thảo dược. Mặt kh&aacute;c, nếu kh&ocirc;ng th&iacute;ch đắng th&igrave; bạn c&oacute; thể uống nước s&acirc;m b&igrave;nh thường c&oacute; vị ngọt thanh rất dễ uống.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/29/sdvsd-15538410656331812239909.jpg\" target=\"_blank\" title=\"\"><img alt=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 8.\" id=\"img_3c0f5400-51ec-11e9-a1e3-fd22c850312c\" src=\"https://kenh14cdn.com/2019/3/29/sdvsd-15538410656331812239909.jpg\" title=\"Team Sài Gòn phải chịu nắng gắt thì không thể thiếu những món tráng miệng thảo mộc mát người này - Ảnh 8.\" /></a></p>\r\n', 1554017237, 'http://localhost:8080/restaurant/uploads/amthucviahe.jpg', 'Team Sài Gòn hãy bỏ túi các món từ thảo mộc sau đây để \"hạ hoả\" trong tiết trời nóng bức để yên tâm làm việc, học tập.'),
(8, 'Khám phá \"phản đề\" của Fast Food: phong trào Slow Food và những giá trị truyền thống', 32, '<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">C&oacute; thể bạn kh&ocirc;ng biết, đang c&oacute; một &quot;cuộc chiến&quot; vẫn lu&ocirc;n lặng lẽ diễn ra nhiều năm nay trong giới ẩm thực. Đ&oacute; kh&ocirc;ng phải l&agrave; một cuộc chiến c&aacute; nh&acirc;n giữa hai đầu bếp t&agrave;i ba n&agrave;o đ&oacute;, cũng kh&ocirc;ng phải cuộc chiến với quy m&ocirc; v&ugrave;ng miền như thức ăn v&ugrave;ng n&agrave;y &quot;đấu&quot; với thức ăn v&ugrave;ng kh&aacute;c, đ&oacute; cũng kh&ocirc;ng phải l&agrave; cuộc cạnh tranh giữa hai thương hiệu.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Đ&acirc;y l&agrave; một cuộc chiến rộng hơn thế rất nhiều giữa hai trường ph&aacute;i &quot;nhanh&quot; v&agrave; &quot;chậm&quot;. Đ&acirc;y ch&iacute;nh l&agrave; cuộc chiến giữa fast food (thức ăn nhanh) v&agrave; kh&aacute;i niệm vừa quen vừa lạ: slow food (thức ăn hữu cơ, truyền thống).</span></span></span></p>\r\n\r\n<h3 style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:26px\"><span style=\"font-family:SFD-Bold\"><span style=\"color:#111111\"><span style=\"background-color:#ffffff\"><span style=\"color:#e69138\">Slow food l&agrave; g&igrave;? Kh&aacute;i niệm slow food đ&atilde; ra đời như thế n&agrave;o?</span></span></span></span></span></h3>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Mọi thứ bắt đầu v&agrave;o một ng&agrave;y h&egrave; đẹp trời năm 1985, người Mỹ đem cửa h&agrave;ng McDonald&rsquo;s đầy tự h&agrave;o của m&igrave;nh đặt tại con phố sầm uất nhất Roma, gần c&aacute;c nh&agrave; h&agrave;ng pizza trứ danh v&agrave; những nh&agrave; mốt nổi tiếng. Tr&aacute;i với sự hồ hởi của người Mỹ, chủ cửa h&agrave;ng Valentino mi&ecirc;u tả &ocirc;ng h&agrave;ng x&oacute;m bằng giọng điệu đầy phẫn nộ:&nbsp;<em>&quot;Họ (Mcdonalds) đ&atilde; đem đến thứ tiếng ồn khủng khiếp v&agrave; một m&ugrave;i chi&ecirc;n x&agrave;o kh&ocirc;ng thể chịu nổi bao tr&ugrave;m kh&ocirc;ng kh&iacute;!&quot;</em>.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Vậy l&agrave; cuộc chiến kh&ocirc;ng hồi kết giữa fast food (thức ăn nhanh) v&agrave; slow food (thức ăn hữu cơ, truyền thống) đ&atilde; bắt đầu như vậy đấy.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a href=\"https://kenh14cdn.com/2019/3/28/1-1553739800737496289044.jpg\" title=\"\"><img alt=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 1.\" class=\"lightbox-content\" id=\"img_76175040-5100-11e9-8b95-3fd80f2d5dc6\" src=\"https://kenh14cdn.com/2019/3/28/1-1553739800737496289044.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:bottom; width:580px\" title=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 1.\" /></a></span></span></span></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:10px; text-align:left\">\r\n<p style=\"margin-left:0px; margin-right:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:inherit\"><span style=\"background-color:#f2f2f2\"><em><span style=\"font-family:inherit\"><span style=\"color:#666666\">&quot;McDonald&rsquo;s đ&atilde; lỡ &quot;đụng chạm&quot; đến &Yacute; khi quyết định mở một nh&agrave; h&agrave;ng ở ngay Th&agrave;nh Vatican&quot; (Nguồn: Thedailymeal).</span></span></em></span></span></span></span></span></p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Tự h&agrave;o với nền ẩm thực cổ truyền h&agrave;ng trăm năm, nguy&ecirc;n liệu tươi ngon lấy từ những bờ biển v&agrave; đồng cổ xung quanh c&ugrave;ng c&aacute;c c&ocirc;ng thức cầu k&igrave; lu&ocirc;n được l&agrave;m thủ c&ocirc;ng (made from scratch), người &Yacute; kh&ocirc;ng thể n&agrave;o chấp nhận &yacute; tưởng về những m&oacute;n ăn dầu mỡ, nấu nhanh ăn nhanh v&agrave; no cũng nhanh do McDonald&rsquo;s mang đến.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Dẫu cả ch&acirc;u &Acirc;u khi đ&oacute; đang say đắm với sự tiện lợi của fast food, nước &Yacute; quyết bảo vệ những gi&aacute; trị ẩm thực truyền thống trước &ocirc;ng khổng lồ người Mỹ. Bằng chứng l&agrave; tổ chức Arigola&nbsp;được th&agrave;nh lập v&agrave;o&nbsp;năm 1986 nhằm chống lại sự mở rộng của McDonald&rsquo;s tại Rome, v&agrave; dần dần ph&aacute;t triển th&agrave;nh tổ chức Slow Food với h&agrave;ng trăm chi nh&aacute;nh tr&ecirc;n thế giới. Slow food l&agrave; một phản đề của fast food, với niềm tin về những gi&aacute; trị l&acirc;u d&agrave;i của ẩm thực, hơn l&agrave; sự tiện lợi nhất thời.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:15px; margin-left:15px; margin-right:15px; margin-top:15px; padding:0px; text-align:center; width:auto\">\r\n<div class=\"border-left name-n-quote\" style=\"border-bottom-color:initial; border-bottom-style:initial; border-image:initial; border-left-color:#ff6c13; border-left-style:solid; border-right-color:#ff6c13; border-right-style:initial; border-top-color:initial; border-top-style:initial; border-width:1px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px 0px 0px 20px\">\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:22px !important\"><span style=\"font-family:SFD-Light !important\"><span style=\"color:#222222\">Slow food l&agrave; một phản đề của fast food, với niềm tin về những gi&aacute; trị l&acirc;u d&agrave;i của ẩm thực, hơn l&agrave; sự tiện lợi nhất thời.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\">&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<h3 style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:26px\"><span style=\"font-family:SFD-Bold\"><span style=\"color:#111111\"><span style=\"background-color:#ffffff\"><strong><span style=\"color:#e69138\">Slow food - hiện th&acirc;n của c&aacute;c gi&aacute; trị ẩm thực kh&oacute; c&oacute; thể thay thế&nbsp;</span></strong></span></span></span></span></h3>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Kh&ocirc;ng thể phủ nhận những tiện &iacute;ch thức ăn nhanh đem lại cho cuộc sống hiện đại: M&oacute;n ăn chế biến nhanh hơn, chất lượng trăm c&aacute;i ho&agrave;n hảo như một v&agrave; m&ugrave;i vị cũng kh&ocirc;ng hề tệ. Thế nhưng, fast food đ&atilde; tước đi một trong những &quot;gia vị&quot; quan trọng nhất của ẩm thực - ch&iacute;nh l&agrave; thời gian.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Cuộc b&agrave;i trừ McDonald&rsquo;s của người &Yacute; c&oacute; thể bị xem l&agrave; bảo thủ, cho đến khi ch&uacute;ng ta nh&igrave;n v&agrave;o lịch sử ẩm thực nước n&agrave;y. Tất cả những đặc sản tinh hoa như ph&ocirc; mai, pizza, thịt x&ocirc;ng kh&oacute;i đều kh&ocirc;ng thể nấu nhanh. Nhẹ nh&agrave;ng th&igrave; ủ v&agrave;i tiếng, k&igrave; c&ocirc;ng c&oacute; thể &quot;ngậm hơi đất&quot; v&agrave;i chục năm. Sự tỉ mỉ v&agrave; ki&ecirc;n nhẫn trong kh&acirc;u chế biến l&agrave; ch&igrave;a kh&oacute;a l&agrave;m n&ecirc;n gi&aacute; trị ẩm thực &Yacute; n&oacute;i ri&ecirc;ng, v&agrave; nhiều nền ẩm thực lớn n&oacute;i chung.&nbsp;</span></span></span></p>\r\n\r\n<div class=\"fl mt-10 wp100\" id=\"admzone474524\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:10px; padding:0px; text-align:start; width:580px\">\r\n<div id=\"ads_zone474524\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<div id=\"ads_zone474524_slot1\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<div class=\"banner0\" id=\"ads_zone474524_banner566957\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<div id=\"ssppagebid_1998\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<div class=\"banner0\" id=\"nativezone_504836_3195\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">&nbsp;</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode noCaption\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"detail-img-lightbox\" href=\"https://kenh14cdn.com/2019/3/28/3-15537401652401123169537.jpg\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:zoom-in; display:block\" target=\"_blank\" title=\"\"><img alt=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 3.\" class=\"lightbox-content\" id=\"img_4e807790-5101-11e9-8f33-fd3245c1644d\" src=\"https://kenh14cdn.com/2019/3/28/3-15537401652401123169537.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:bottom; width:580px\" title=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 3.\" /></a></span></span></span></div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">&Yacute; thức được điều đ&oacute;, tổ chức Slow Food đ&atilde; hoạt động với t&ocirc;n chỉ nấu chậm, ăn chậm, tốt cho sức khỏe v&agrave; t&ocirc;n trọng m&ocirc;i trường. Tổ chức c&oacute; những gạch đầu d&ograve;ng r&otilde; r&agrave;ng để bảo tồn phong c&aacute;ch ăn chậm, v&iacute; dụ như sử dụng c&aacute;c phương thức chế biến tốn thời gian nhưng tốt cho sức khỏe như hầm, ninh v&agrave; ưu ti&ecirc;n d&ugrave;ng nguy&ecirc;n liệu hữu cơ, trồng tại địa phương. Cần tr&aacute;nh chi&ecirc;n x&agrave;o v&agrave; sử dụng nguy&ecirc;n liệu kh&ocirc;ng tự nhi&ecirc;n.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Slow food y&ecirc;u cầu người ta tạm g&aacute;c lại những lợi &iacute;ch nhất thời, học c&aacute;ch ăn sao cho tốt với m&igrave;nh lẫn cả m&ocirc;i trường, mới c&oacute; thể duy tr&igrave; nền ẩm thực thế giới d&agrave;i l&acirc;u.&nbsp;Slow-food y&ecirc;u cầu người ta tạm g&aacute;c lại những lợi &iacute;ch nhất thời, học c&aacute;ch ăn sao cho tốt với m&igrave;nh lẫn cả m&ocirc;i trường, mới c&oacute; thể duy tr&igrave; nền ẩm thực thế giới d&agrave;i l&acirc;u.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode alignRight\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:20px; margin-left:30px; margin-right:-40px; margin-top:15px; padding:0px; text-align:center; width:auto\">\r\n<div class=\"border-left name-n-quote\" style=\"border-bottom-color:initial; border-bottom-style:initial; border-image:initial; border-left-color:#ff6c13; border-left-style:solid; border-right-color:#ff6c13; border-right-style:initial; border-top-color:initial; border-top-style:initial; border-width:1px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px 0px 0px 20px\">\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:22px !important\"><span style=\"font-family:SFD-Light !important\"><span style=\"color:#222222\">Slow food tồn tại để bảo vệ những gi&aacute; trị truyền thống, v&agrave; chắc rằng ch&uacute;ng kh&ocirc;ng bị l&atilde;ng qu&ecirc;n.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\">&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Mặt kh&aacute;c, cần phải chỉ ra một điều về slow food, rằng kh&aacute;i niệm n&agrave;y kh&ocirc;ng ra đời với mục ti&ecirc;u duy nhất l&agrave; &quot;hạ bệ&quot; fast food, ho&agrave;n to&agrave;n kh&ocirc;ng. fast food c&oacute; c&aacute;i hay của ri&ecirc;ng n&oacute; cũng như những gi&aacute; trị nhất định ph&ugrave; hợp với x&atilde; hội v&agrave; nhịp độ sống c&ugrave;ng nhu cầu của con người hiện đại. Đ&uacute;ng vậy, slow food kh&ocirc;ng ra đời để &quot;d&igrave;m&quot; fast food, slow food tồn tại để bảo vệ những gi&aacute; trị truyền thống, v&agrave; chắc rằng ch&uacute;ng kh&ocirc;ng bị l&atilde;ng qu&ecirc;n.</span></span></span></p>\r\n\r\n<h3 style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:26px\"><span style=\"font-family:SFD-Bold\"><span style=\"color:#111111\"><span style=\"background-color:#ffffff\"><strong><span style=\"color:#e69138\">Slow food v&agrave; nền ẩm thực Việt</span></strong></span></span></span></span></h3>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Năm 2013, một Việt Kiều tại &Yacute; đ&atilde; chiến thắng giải thưởng nấu ăn của hiệp hội Slow Food, với một b&iacute; quyết m&agrave; mỗi đứa trẻ mang d&ograve;ng m&aacute;u Việt Nam đều kh&ocirc;ng xa lạ g&igrave;: nước mắm. Dường như lu&ocirc;n c&oacute; một mối li&ecirc;n hệ s&acirc;u sắc giữa tinh thần slow food của nước &Yacute; với ẩm thực truyền thống Việt Nam.&nbsp;</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:15px; margin-left:15px; margin-right:15px; margin-top:15px; padding:0px; text-align:center; width:auto\">\r\n<div class=\"border-left name-n-quote\" style=\"border-bottom-color:initial; border-bottom-style:initial; border-image:initial; border-left-color:#ff6c13; border-left-style:solid; border-right-color:#ff6c13; border-right-style:initial; border-top-color:initial; border-top-style:initial; border-width:1px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px 0px 0px 20px\">\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:22px !important\"><span style=\"font-family:SFD-Light !important\"><span style=\"color:#222222\">Hai gi&aacute; trị quan trọng nhất trong slow food &ndash; sử dụng nguy&ecirc;n liệu địa phương gần gũi, v&agrave; nấu nướng ăn uống với tốc độ chậm &ndash; đều thể hiện r&otilde; n&eacute;t trong ẩm thực h&agrave;ng ng&agrave;y của ch&uacute;ng ta.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\">&nbsp;</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Quả nhi&ecirc;n nếu nh&igrave;n lại, người Việt l&agrave; bậc thầy trong việc biến những thứ cỏ nội hoa h&egrave;n trong vườn th&agrave;nh thức ăn ngon. M&ugrave;a g&igrave; thức nấy, &quot;thu ăn măng tr&uacute;c đ&ocirc;ng ăn gi&aacute;&quot;, cả nền ẩm thực trăm năm của Việt Nam c&oacute; thể t&oacute;m gọn lại bằng bốn m&ugrave;a v&agrave; ba miền: M&ugrave;a h&egrave; oi nồm c&oacute; b&uacute;n ốc nguội, m&iacute;a ướp lạnh, s&acirc;m bổ lượng m&aacute;t người, m&ugrave;a thu se se c&oacute; hương cốm l&agrave;ng V&ograve;ng, m&ugrave;a đ&ocirc;ng c&oacute; đủ loại đặc sản vừa ăn vừa h&iacute;t h&agrave; như khoai nướng, hạt dẻ, ch&egrave; n&oacute;ng, v.v&hellip; Việc sử dụng những thực phẩm theo m&ugrave;a đem lại loạt gi&aacute; trị kh&ocirc;ng ngờ cho ẩm thực Việt. Thứ nhất, m&oacute;n v&agrave;o m&ugrave;a l&uacute;c n&agrave;o cũng ngon hơn tr&aacute;i m&ugrave;a. Thứ hai, ăn đ&uacute;ng với đặc t&iacute;nh thời tiết gi&uacute;p cơ thể th&iacute;ch nghi tốt hơn, tăng cường sức khỏe giống n&ograve;i r&otilde; rệt, V&agrave; cuối c&ugrave;ng, nhờ sống thuận theo tuần ho&agrave;n của tự nhi&ecirc;n m&agrave; c&acirc;y tr&aacute;i, lương thực của nền ẩm thực ấy lu&ocirc;n c&oacute; thể tự sản sinh v&agrave; dồi d&agrave;o.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a href=\"https://kenh14cdn.com/2019/3/28/9-155374035307490116499.jpg\" title=\"Cốm Hà Nội, một đặc sản chỉ có vào mùa thu thể hiện tinh thần \"><img alt=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 6.\" class=\"lightbox-content\" id=\"img_bfabb830-5101-11e9-98e3-b186edc4f4d1\" src=\"https://kenh14cdn.com/2019/3/28/9-155374035307490116499.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:bottom; width:580px\" title=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 6.\" /></a></span></span></span></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:10px; text-align:left\">\r\n<p style=\"margin-left:0px; margin-right:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:inherit\"><span style=\"background-color:#f2f2f2\"><em><span style=\"font-family:inherit\"><span style=\"color:#666666\">Cốm H&agrave; Nội, một đặc sản chỉ c&oacute; v&agrave;o m&ugrave;a thu thể hiện tinh thần &quot;m&ugrave;a g&igrave; thức nấy&quot;.</span></span></em></span></span></span></span></span></p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Mặt kh&aacute;c, nếu c&oacute; đủ nguy&ecirc;n liệu đ&uacute;ng m&ugrave;a, hữu cơ thơm ngon trong nh&agrave; rồi, &aacute;p dụng phương thức chế biến nhanh gọn lẹ của fast food th&igrave; cũng&hellip; x&ocirc;i hỏng bỏng kh&ocirc;ng. H&atilde;y nh&igrave;n v&agrave;o m&acirc;m cơm đơn giản h&agrave;ng ng&agrave;y th&ocirc;i, b&ecirc;n trong cũng phải c&oacute; một m&oacute;n canh, một m&oacute;n thịt, một m&oacute;n x&agrave;o. Vị chi nấu được cả m&acirc;m n&oacute;ng sốt cho đ&uacute;ng nguy&ecirc;n tắc ngũ h&agrave;nh cũng phải kh&ocirc;ng dưới 30 ph&uacute;t. Đ&oacute; l&agrave; chưa kể c&aacute;c phương thức chế biến như kho, hầm, rồi đỉnh cao l&agrave; ninh xương h&agrave;ng tiếng đồng hồ cho ra nước d&ugrave;ng quốc hồn quốc t&uacute;y trong phở v&agrave; b&uacute;n &ndash; đều chẳng nhanh gọn g&igrave;. Nấu đ&atilde; l&acirc;u m&agrave; ăn cũng kh&ocirc;ng thể vội. L&agrave;m sao người ta c&oacute; thể bỏ hộp một b&aacute;t phở như chiếc b&aacute;nh hamburger, &quot;x&aacute;ch&quot; n&oacute; b&ecirc;n m&igrave;nh đi khắp phố phường, khi rảnh rang th&igrave; tranh thủ &quot;l&agrave;m miếng&quot;.&nbsp;Sự tận hưởng chậm r&atilde;i, những bữa cơm h&agrave;ng ng&agrave;y cầu k&igrave;, th&oacute;i quen vừa nh&acirc;m nhi vừa tận hưởng kh&ocirc;ng kh&iacute; v&agrave; cuộc n&oacute;i chuyện xung quanh dường như đ&atilde; nằm s&acirc;u trong m&aacute;u người Việt.&nbsp;</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode active\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"detail-img-lightbox\" href=\"https://kenh14cdn.com/2019/3/28/8-1553740274127475719073.jpg\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:zoom-in; display:block\" target=\"_blank\" title=\"Mâm cơm thường ngày của người Việt gần như luôn phải có một canh, một mặn, một xào, một chay.\"><img alt=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 7.\" class=\"lightbox-content\" id=\"img_8f75a5e0-5101-11e9-9e56-1714e67010dd\" src=\"https://kenh14cdn.com/2019/3/28/8-1553740274127475719073.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; margin:0px; max-width:100%; padding:0px; vertical-align:bottom; width:580px\" title=\"Khám phá phản đề của Fast Food: phong trào Slow Food và những giá trị truyền thống - Ảnh 7.\" /></a></span></span></span></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:10px; text-align:left\">\r\n<p style=\"margin-left:0px; margin-right:0px\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><span style=\"font-size:inherit\"><span style=\"background-color:#f2f2f2\"><em><span style=\"font-family:inherit\"><span style=\"color:#666666\">M&acirc;m cơm thường ng&agrave;y của người Việt gần như lu&ocirc;n phải c&oacute; một canh, một mặn, một x&agrave;o, một chay.</span></span></em></span></span></span></span></span></p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Sau một chuyến c&ocirc;ng du nước ngo&agrave;i, thưởng thức b&aacute;t phở xứ người c&oacute; vị kh&ocirc;ng thể nhận ra, đệ nhất ẩm thực gia Nguyễn Tu&acirc;n đ&atilde; ngộ ra điều n&agrave;y:&nbsp;<em>&quot;Phở b&aacute;t của ch&uacute;ng ta kh&ocirc;ng thể th&agrave;nh thứ đồ đ&oacute;ng hộp được đ&acirc;u!&quot;</em>. &Ocirc;ng chỉ muốn nhanh nhanh ch&oacute;ng ch&oacute;ng trở về Việt Nam, ăn m&oacute;n Việt Nam đ&uacute;ng nghĩa, bởi&nbsp;<em>&quot;B&aacute;t phở Bắc ăn ở giữa đầu h&egrave; di cư, kh&ocirc;ng bao giờ c&oacute; thể ngon bằng b&aacute;t phở cổ truyền H&agrave; Nội ngay b&ecirc;n l&ograve; than quả b&agrave;ng đỏ giữa ng&agrave;n năm vạn vật n&agrave;y&quot;.</em></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">&Acirc;u cũng chả phải lỗi của đồ đ&oacute;ng hộp hay fast food. Chỉ l&agrave; &quot;good things don&rsquo;t come easy&quot; (Những điều tốt đẹp chẳng đến dễ d&agrave;ng), đ&ocirc;i khi đợi chờ l&agrave; hạnh ph&uacute;c bởi n&oacute; mang lại kết quả thoả m&atilde;n gấp nhiều lần.</span></span></span></p>\r\n', 1554017392, 'http://localhost:8080/restaurant/uploads/8-1553740274127475719073.jpg', 'Tưởng chừng như sinh ra chỉ để '),
(9, 'Mở ra chân trời mới khi nhìn người ta \"bóc tem\" hộp bento Nhật Bản có giá hơn 40 triệu đồng', 35, '<p>Mới đ&acirc;y, trang Buzzfeed đ&atilde; kh&aacute;m ph&aacute; hộp cơm bento Nhật Bản c&oacute; gi&aacute; 1593 USD (180,000 yen) được b&aacute;n tại Nikko (tỉnh Tochigi, Nhật Bản), bởi c&ocirc;ng ty Nikko Masuzushi Honpo. Hộp cơm đắt đỏ n&agrave;y c&ograve;n được biết đến với t&ecirc;n &quot;Hộp bento kho b&aacute;u bị ch&ocirc;n v&ugrave;i Nikko&quot;, bởi v&igrave; to&agrave;n th&acirc;n hộp bento từ hộp đựng cho đến th&agrave;nh phần b&ecirc;n trong đều qu&yacute; gi&aacute; như v&agrave;ng vậy - &ocirc;ng Koichi Tamura, gi&aacute;m đốc c&ocirc;ng ty Nikko Masuzushi Honpo đ&atilde; cho hay.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-92218-am-15536556670102006172750.png\" target=\"_blank\" title=\"Có huyền cơ gì trong hộp bento Nhật Bản giá 45 triệu đồng?\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 1.\" id=\"img_95427090-503c-11e9-b2fb-6f73c338dedc\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-92218-am-15536556670102006172750.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 1.\" /></a></p>\r\n\r\n<p>C&oacute; huyền cơ g&igrave; trong hộp bento Nhật Bản gi&aacute; 45 triệu đồng?</p>\r\n\r\n<p>Nếu bạn c&ograve;n thắc mắc kh&ocirc;ng biết c&oacute; g&igrave; b&ecirc;n trong m&agrave; lại đắt xắt ra miếng, th&igrave; h&atilde;y c&ugrave;ng ch&uacute;ng m&igrave;nh theo ch&acirc;n Buzzfeed kh&aacute;m ph&aacute; nh&eacute;:</p>\r\n\r\n<h3>Đầu ti&ecirc;n l&agrave; hộp đựng</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-92300-am-1553653860579385225876.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 2.\" id=\"img_5f7c67d0-5038-11e9-94d7-39cbf763c067\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-92300-am-1553653860579385225876.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 2.\" /></a></p>\r\n\r\n<p>Hoa văn tr&ecirc;n hộp đựng được tạo n&ecirc;n bởi kỹ thuật đi&ecirc;u khắc truyền thống của tỉnh Tochigi, kỹ thuật n&agrave;y c&oacute; t&ecirc;n l&agrave; Nikko-bori. Đ&acirc;y l&agrave; một loại h&igrave;nh nghệ thuật cổ truyền l&acirc;u đời của th&agrave;nh phố Nikko, được người d&acirc;n bản xứ tự h&agrave;o v&agrave; xem trọng. Kỹ thuật n&agrave;y đ&atilde; c&oacute; từ thời Edo, tức thế kỷ 17 v&agrave; được lưu truyền như một nghề truyền thống cho đến hiện nay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, hộp cơm c&ograve;n được g&oacute;i lại bằng furoshiki - một kỹ thuật g&oacute;i qu&agrave; truyền thống bằng vải của người Nhật.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-93700-am-15536542396971910160275.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 4.\" id=\"img_4253fff0-5039-11e9-9055-ed390aa66c3c\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-93700-am-15536542396971910160275.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 4.\" /></a></p>\r\n\r\n<h3>Th&agrave;nh phần trong hộp bao gồm thịt b&ograve;</h3>\r\n\r\n<p>Thịt b&ograve; trong hộp l&agrave; thịt b&ograve; Wagyu thượng hạng từ Nhật Bản, với gi&aacute; c&oacute; thể l&ecirc;n đến 50 USD cho chỉ 100g. Cụ thể, b&ograve; Wagyu được d&ugrave;ng trong hộp l&agrave; giống b&ograve; đen, được đ&aacute;nh gi&aacute; l&agrave; A5 Wagyu đến từ tỉnh Tochigi. C&aacute;c miếng b&ograve; fillet trong hộp được nấu một c&aacute;ch ho&agrave;n hảo bằng kỹ năng đi&ecirc;u luyện của đầu bếp, với vỏ ch&iacute;n b&ecirc;n ngo&agrave;i v&agrave; giữ được độ ẩm mềm mượt b&ecirc;n trong.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-94629-am-15536548783101537371180.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 5.\" id=\"img_be84a4c0-503a-11e9-a098-c98014c4a5fe\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-94629-am-15536548783101537371180.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 5.\" /></a></p>\r\n\r\n<p>Những miếng thịt b&ograve; mềm đến mức được đ&aacute;nh gi&aacute; l&agrave; &quot;kẹo marshmallow vị thịt b&ograve;&quot;.</p>\r\n\r\n<h3>T&ocirc;m</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95058-am-15536551024881314716776.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 6.\" id=\"img_44ca9350-503b-11e9-b439-4fb8b0f0009e\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95058-am-15536551024881314716776.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 6.\" /></a></p>\r\n\r\n<p>T&ocirc;m được d&ugrave;ng l&agrave; t&ocirc;m Kurama, l&agrave; loại nguy&ecirc;n liệu thượng hạng kể từ thời xa xưa. T&ocirc;m Kurama được cho l&agrave; c&oacute; vị umami đậm đ&agrave;, khiến người ta nhớ m&atilde;i kh&ocirc;ng qu&ecirc;n. T&ocirc;m được luộc chỉ vừa đủ để vỏ ngo&agrave;i chuyển m&agrave;u đỏ, sau đ&oacute; được mang đi nướng để giữ được độ ẩm, kh&ocirc;ng bị kh&ocirc;.</p>\r\n\r\n<h3>Masuzushi</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95412-am-15536555419561669863541.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 7.\" id=\"img_494aced0-503c-11e9-92a2-8d0f4958e9e2\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95412-am-15536555419561669863541.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 7.\" /></a></p>\r\n\r\n<p>Masuzushi l&agrave; loại sushi l&agrave;m từ c&aacute; hồi thượng hạng. Ở Nikko, người ta kh&ocirc;ng thể chế biến lượng lớn masuzushi. Mỗi ng&agrave;y, chỉ c&oacute; khoảng 200 phần được l&agrave;m cẩn thận bằng tay, được n&eacute;n cẩn thận th&agrave;nh từng khối vu&ocirc;ng vức khiến hương vị cũng bị n&eacute;n, tạo n&ecirc;n sự &quot;b&ugrave;ng nổ&quot; khi bắt đầu ăn.</p>\r\n\r\n<h3>Da đậu phụ với trứng c&aacute; tầm</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95619-am-15536556182531704692551.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 8.\" id=\"img_7672b6c0-503c-11e9-ac5f-03c4b11b3af8\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95619-am-15536556182531704692551.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 8.\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng cần giới thiệu cũng c&oacute; thể thấy được trứng c&aacute; tầm (caviar) l&agrave; loại nguy&ecirc;n liệu đắt đỏ bậc nhất. Trong hộp bento n&agrave;y kh&ocirc;ng chỉ c&oacute; tượng trưng v&agrave;i muỗng trứng c&aacute; tầm, m&agrave; c&ograve;n c&oacute; cả một lọ được phủ đầy tr&ecirc;n da đậu phụ. Ph&iacute;a tr&ecirc;n c&ograve;n c&oacute; một &iacute;t v&agrave;ng miếng thật.</p>\r\n\r\n<h3>Cua ho&agrave;ng đế hấp</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95837-am-1553655595034255217149.png\" target=\"_blank\" title=\"\"><img alt=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 9.\" id=\"img_6b001c60-503c-11e9-88cb-b35bd126776a\" src=\"https://kenh14cdn.com/2019/3/27/screen-shot-2019-03-27-at-95837-am-1553655595034255217149.png\" title=\"Mở ra chân trời mới khi nhìn người ta bóc tem hộp bento Nhật Bản có giá hơn 40 triệu đồng - Ảnh 9.\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng phải cua thường, m&agrave; l&agrave; cua ho&agrave;ng đế. V&agrave; cũng kh&ocirc;ng phải cua ho&agrave;ng đế thường, m&agrave; l&agrave; cua ho&agrave;ng đế khổng lồ. Được biết, cua ho&agrave;ng đế d&ugrave;ng cho hộp bento n&agrave;y được gửi đến từ Hokkaido ngay sau khi đ&aacute;nh bắt, v&agrave; chỉ d&ugrave;ng cua đực, bởi v&igrave; hương vị cua c&aacute;i sẽ bị thay đổi khi mang trứng.</p>\r\n\r\n<h3>Tạm kết:</h3>\r\n\r\n<p>Theo bạn, so với những nguy&ecirc;n liệu n&agrave;y th&igrave; gi&aacute; cả của chiếc hộp c&oacute; phải l&agrave; qu&aacute; đắt hay kh&ocirc;ng? Nhiều người đ&atilde; cho rằng nh&igrave;n một loạt những nguy&ecirc;n liệu, c&aacute;ch chế biến đến c&aacute;ch tr&igrave;nh b&agrave;y, th&igrave; c&aacute;i gi&aacute; 45 triệu đồng cho hộp bento n&agrave;y l&agrave; chẳng hề đắt t&iacute; n&agrave;o. Mặt kh&aacute;c, &ocirc;ng Tamura cũng cho hay, bạn phải đặt trước &iacute;t nhất 1 tuần th&igrave; họ mới c&oacute; thể thu gom đủ nguy&ecirc;n liệu để l&agrave;m đấy!</p>\r\n', 1554024738, 'http://localhost:8080/restaurant/uploads/4.jpg', 'Cụ thể, hộp bento có giá 1593 USD tương đương 45 triệu đồng Việt Nam, bằng với 900 đơn vị tô phở.');
INSERT INTO `news` (`id`, `title`, `id_catenews`, `content_news`, `date_created`, `image_news`, `quote`) VALUES
(11, 'Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn', 40, '<p>Tteok kuk c&oacute; nghĩa l&agrave; một sự khởi đầu mới, do đ&oacute;, m&agrave;u trắng được sử dụng cho b&aacute;nh gạo để nhằm mang &yacute; nghĩa l&agrave;m sạch cả cơ thể v&agrave; t&acirc;m tr&iacute;. C&oacute; như vậy th&igrave; v&agrave;o năm mới, con người mới rũ bỏ được những điều kh&ocirc;ng tốt đẹp v&agrave; c&oacute; sự bắt đầu ho&agrave;n hảo hơn.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/2/8/photo-1-15495986559041102592414.jpg\" target=\"_blank\" title=\"Món Tteok kuk được chế biến từ Tteok thái lát, có màu trắng tinh, nấu với nước xương bò hầm và các loại gia vị đặc trưng của Hàn Quốc.\"><img alt=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 1.\" id=\"img_327f1850-2b57-11e9-bf2c-33f88c4309df\" src=\"https://kenh14cdn.com/2019/2/8/photo-1-15495986559041102592414.jpg\" title=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 1.\" /></a></p>\r\n\r\n<p>M&oacute;n Tteok kuk được chế biến từ Tteok th&aacute;i l&aacute;t, c&oacute; m&agrave;u trắng tinh, nấu với nước xương b&ograve; hầm v&agrave; c&aacute;c loại gia vị đặc trưng của H&agrave;n Quốc.</p>\r\n\r\n<p>Nếu sử dụng b&aacute;nh gạo trắng d&agrave;i th&igrave; &yacute; nghĩa tiềm ẩn b&ecirc;n trong l&agrave; muốn mang đến cuộc sống khỏe mạnh v&agrave; trường thọ hơn. Do đ&oacute;, khi ăn b&aacute;nh gạo d&agrave;i, người H&agrave;n Quốc tự nhủ rằng kh&ocirc;ng n&ecirc;n l&agrave;m đứt miếng b&aacute;nh gạo trước khi ăn, ngược lại nếu k&eacute;o b&aacute;nh gạo ra c&agrave;ng d&agrave;i sẽ c&agrave;ng tốt.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/2/8/photo-1-15495986601261758974496.jpg\" target=\"_blank\" title=\"Tteok kuk có nghĩa là một sự khởi đầu mới, do đó, màu trắng được sử dụng cho bánh gạo để nhằm mang ý nghĩa làm sạch cả cơ thể và tâm trí.\"><img alt=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 2.\" id=\"img_348807b0-2b57-11e9-988e-59cb425655ab\" src=\"https://kenh14cdn.com/2019/2/8/photo-1-15495986601261758974496.jpg\" title=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 2.\" /></a></p>\r\n\r\n<p>Tteok kuk c&oacute; nghĩa l&agrave; một sự khởi đầu mới, do đ&oacute;, m&agrave;u trắng được sử dụng cho b&aacute;nh gạo để nhằm mang &yacute; nghĩa l&agrave;m sạch cả cơ thể v&agrave; t&acirc;m tr&iacute;.</p>\r\n\r\n<p>Ngo&agrave;i &yacute; nghĩa trường thọ, b&aacute;nh gạo d&agrave;i c&ograve;n tượng trưng cho sự thịnh vượng về t&agrave;i sản v&agrave; tiền t&agrave;i. Bởi chiều d&agrave;i của chiếc b&aacute;nh gạo được v&iacute; như sự gi&agrave;u c&oacute; của gia đ&igrave;nh. Song song đ&oacute;, b&aacute;nh gạo c&oacute; dạng h&igrave;nh tr&ograve;n cũng tượng trưng cho mong ước ph&aacute;t t&agrave;i v&agrave; kiếm được nhiều tiền hơn v&agrave;o năm mới.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/2/8/photo-2-1549598660129883659334.jpg\" target=\"_blank\" title=\"\"><img alt=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 3.\" id=\"img_348ebe70-2b57-11e9-bf2c-33f88c4309df\" src=\"https://kenh14cdn.com/2019/2/8/photo-2-1549598660129883659334.jpg\" title=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 3.\" /></a></p>\r\n\r\n<p>Chiều d&agrave;i của chiếc b&aacute;nh gạo được v&iacute; như sự gi&agrave;u c&oacute; của gia đ&igrave;nh.</p>\r\n\r\n<p>Đặc biệt, m&oacute;n canh b&aacute;nh gạo v&agrave;o năm mới ở H&agrave;n Quốc kh&ocirc;ng hẳn chỉ c&oacute; m&agrave;u trắng bởi ngo&agrave;i b&aacute;nh gạo th&igrave; m&oacute;n canh n&agrave;y c&ograve;n c&oacute; sự tham gia của nhiều nguy&ecirc;n liệu kh&aacute;c như trứng, rong biển, c&aacute; tuyết, thịt b&ograve;, t&ocirc;m, b&agrave;o ngư, g&agrave;,... Do đ&oacute;, tuy nguy&ecirc;n liệu ch&iacute;nh l&agrave; b&aacute;nh gạo nhưng m&oacute;n canh n&agrave;y lại c&oacute; rất nhiều vị đậm đ&agrave;.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/2/8/photo-3-1549598660131409184479.jpg\" target=\"_blank\" title=\"Đây là món ăn không thể thiếu trong dịp đầu năm mới của người dân Hàn Quốc.\"><img alt=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 4.\" id=\"img_9d708690-2b56-11e9-99b1-8df8ec6bef2a\" src=\"https://kenh14cdn.com/2019/2/8/photo-3-1549598660131409184479.jpg\" title=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 4.\" /></a></p>\r\n\r\n<p>Đ&acirc;y l&agrave; m&oacute;n ăn kh&ocirc;ng thể thiếu trong dịp đầu năm mới của người d&acirc;n H&agrave;n Quốc.</p>\r\n\r\n<p>B&aacute;nh Tteok, từ thời xưa, được biết đến l&agrave; m&oacute;n b&aacute;nh gạo cung đ&igrave;nh, l&agrave; một m&oacute;n ăn sơn h&agrave;o hải vị chỉ được phục vụ trong triều đ&igrave;nh. M&oacute;n ăn n&agrave;y được chế biến từ b&aacute;nh gạo l&uacute;a m&igrave; trắng chi&ecirc;n với thịt b&ograve;, bầu l&aacute;t kh&ocirc;, đậu xanh, hạt mầm, rau m&ugrave;i t&acirc;y, nấm Shiitake, c&agrave; rốt, h&agrave;nh l&aacute;, v&agrave; nước tương. Tuy nhi&ecirc;n, b&aacute;nh Tteok ng&agrave;y nay c&oacute; nhiều thay đổi so với b&aacute;nh gạo cung đ&igrave;nh để ph&ugrave; hợp với xu hướng ẩm thực v&agrave; khẩu vị của mọi người.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/2/8/photo-4-15495986601321163539136.jpg\" target=\"_blank\" title=\"Món ăn này được chế biến từ bánh gạo lúa mì trắng chiên với thịt bò, bầu lát khô, đậu xanh, hạt mầm, rau mùi tây, nấm Shiitake, cà rốt, hành lá, và nước tương.\"><img alt=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 5.\" id=\"img_34841010-2b57-11e9-bf2c-33f88c4309df\" src=\"https://kenh14cdn.com/2019/2/8/photo-4-15495986601321163539136.jpg\" title=\"Tteok kuk - món ăn không thể thiếu dịp trong Tết của người Hàn - Ảnh 5.\" /></a></p>\r\n\r\n<p>M&oacute;n ăn n&agrave;y được chế biến từ b&aacute;nh gạo l&uacute;a m&igrave; trắng chi&ecirc;n với thịt b&ograve;, bầu l&aacute;t kh&ocirc;, đậu xanh, hạt mầm, rau m&ugrave;i t&acirc;y, nấm Shiitake, c&agrave; rốt, h&agrave;nh l&aacute;, v&agrave; nước tương.</p>\r\n\r\n<p>C&aacute;c loại b&aacute;nh Tteok đặc biệt hấp dẫn với giới trẻ ng&agrave;y nay l&agrave; Tteok haemul với rau v&agrave; hải sản, b&aacute;nh Tteok japchae với m&igrave; gạo ống v&agrave; rau quả, b&aacute;nh Tteok pho m&aacute;t, b&aacute;nh Tteok c&agrave; ri&hellip;</p>\r\n\r\n<p>B&aacute;nh Tteok thường được đựng trong c&aacute;c khay gỗ c&oacute; phủ khăn lụa rất trang trọng. Sự sắp đặt c&aacute;c loại b&aacute;nh Tteok v&agrave; c&aacute;ch tạo m&agrave;u sắc của b&aacute;nh Tteok từ c&aacute;c nguy&ecirc;n liệu tự nhi&ecirc;n ch&iacute;nh l&agrave; n&eacute;t đặc sắc trong m&oacute;n b&aacute;nh n&agrave;y.</p>\r\n', 1554172832, 'http://localhost:8080/restaurant/uploads/photo-1-15495986601261758974496.jpg', 'Canh bánh gạo (Tteok kuk) là món ăn truyền thống lâu đời và đặc trưng của ẩm thực Hàn Quốc.');
INSERT INTO `news` (`id`, `title`, `id_catenews`, `content_news`, `date_created`, `image_news`, `quote`) VALUES
(12, 'Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua', 47, '<h3 style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:26px\"><span style=\"font-family:SFD-Bold\"><span style=\"color:#111111\"><span style=\"background-color:#ffffff\"><strong>Kam&rsquo;s Roast Goose (</strong><strong>甘牌燒鵝)</strong></span></span></span></span></h3>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Khi đến cửa h&agrave;ng ngỗng quay của Kam th&igrave; thực kh&aacute;ch phải chuẩn bị sẵn t&acirc;m l&yacute; xếp h&agrave;ng chờ kh&aacute; l&acirc;u, th&ocirc;ng thường khoảng 30 ph&uacute;t hơn. Bởi cửa h&agrave;ng ngỗng quay n&agrave;y từng đạt sao Michelin danh gi&aacute; n&ecirc;n kh&aacute;ch h&agrave;ng gh&eacute; qu&aacute;n mỗi ng&agrave;y rất đ&ocirc;ng.</span></span></span></p>\r\n\r\n<div class=\"LayoutAlbumWrapper VCSortableInPreviewMode active alignJustify noCaption\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:22px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div class=\"LayoutAlbumContent\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; width:580px\">\r\n<div class=\"LayoutAlbumRow\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; width:auto\">\r\n<p><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"sp-img-zoom\" href=\"https://kenh14cdn.com/2018/4/28/1a-1524893627459142428345.jpg\" rel=\"lightbox\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:-webkit-zoom-in; color:transparent; display:block; height:268px; position:relative; width:357.328px\" title=\"\"><img alt=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2018/4/28/1a-1524893627459142428345.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; height:268px; margin:0px; max-width:100%; padding:0px; transition:all 0.25s ease-in-out 0s; vertical-align:baseline; width:357.333px\" title=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 1.\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"sp-img-zoom\" href=\"https://kenh14cdn.com/2018/4/28/1b-1524893627461963254099.jpg\" rel=\"lightbox\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:-webkit-zoom-in; color:transparent; display:block; height:268px; position:relative; width:214.391px\" title=\"\"><img alt=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2018/4/28/1b-1524893627461963254099.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; height:268px; margin:0px; max-width:100%; padding:0px; transition:all 0.25s ease-in-out 0s; vertical-align:baseline; width:214.4px\" title=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 1.\" /></a></span></span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Kh&ocirc;ng chỉ thịt ngỗng m&agrave; phần nước sốt ở đ&acirc;y cực ngon, đặc biệt l&agrave; nhờ phương ph&aacute;p ướp thịt gia truyền n&ecirc;n nước sốt thấm rất đều v&agrave;o thịt ngỗng. Thậm ch&iacute;, khi so s&aacute;nh với cửa h&agrave;ng ngỗng quay kh&aacute;c cũng từng đạt giải Michelin th&igrave; nhiều kh&aacute;ch h&agrave;ng vẫn đ&aacute;nh gi&aacute; ngỗng quay ở đ&acirc;y c&oacute; hương vị ngon hơn hẳn.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode active\" id=\"ObjectBoxContent_1524893995499\" style=\"-webkit-text-stroke-width:0px; border:2px solid #fecb2e; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:10px 25px !important; text-align:justify !important; width:550px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<p style=\"margin-left:0px !important; margin-right:0px !important\"><span style=\"font-size:12pt\"><span style=\"font-family:SFD-Medium\"><span style=\"color:#222222\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><strong>Địa chỉ:</strong>&nbsp;226 Ground Floor, Po Wah Commercial Centre, Hennessy Rd, Wan Chai, Hong Kong</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px !important; margin-right:0px !important\"><span style=\"font-size:12pt\"><span style=\"font-family:SFD-Medium\"><span style=\"color:#222222\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><strong>Giờ mở cửa:</strong>&nbsp;11:30 - 21:30</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px !important; margin-right:0px !important\"><span style=\"font-size:12pt\"><span style=\"font-family:SFD-Medium\"><span style=\"color:#222222\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><strong>Gi&aacute; cả:&nbsp;</strong>15 USD (khoảng 314k)</span></span></span></span></span></p>\r\n</div>\r\n</div>\r\n\r\n<h3 style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:26px\"><span style=\"font-family:SFD-Bold\"><span style=\"color:#111111\"><span style=\"background-color:#ffffff\"><strong>Kau Kee Beef Brisket (</strong><strong>九</strong><strong>记牛腩)</strong></span></span></span></span></h3>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Mặc d&ugrave; chưa từng đạt giải sao Michelin nhưng với lịch sử tồn tại hơn 90 năm n&ecirc;n khi đến h&agrave;ng m&igrave; b&ograve; n&agrave;y ở Hong Kong th&igrave; l&uacute;c n&agrave;o bạn cũng nh&igrave;n thấy một h&agrave;ng d&agrave;i người đang chờ đến lượt thưởng thức. C&oacute; lẽ chỉ bao nhi&ecirc;u đ&oacute; th&ocirc;i l&agrave; bạn cũng đ&atilde; c&oacute; thể đ&aacute;nh gi&aacute; được mức độ ngon của c&aacute;c m&oacute;n b&ograve; ở qu&aacute;n ăn n&agrave;y.</span></span></span></p>\r\n\r\n<div class=\"LayoutAlbumWrapper VCSortableInPreviewMode active alignJustify noCaption\" style=\"-webkit-text-stroke-width:0px; border:0px; margin-bottom:22px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; text-align:center; width:580px\">\r\n<div class=\"LayoutAlbumContent\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; width:580px\">\r\n<div class=\"LayoutAlbumRow\" style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px; width:auto\">\r\n<p><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"sp-img-zoom\" href=\"https://kenh14cdn.com/2018/4/28/2b-15248936274642079347010.jpg\" rel=\"lightbox\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:-webkit-zoom-in; color:transparent; display:block; height:245px; position:relative; width:245px\" title=\"\"><img alt=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2018/4/28/2b-15248936274642079347010.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; height:245px; margin:0px; max-width:100%; padding:0px; transition:all 0.25s ease-in-out 0s; vertical-align:baseline; width:245px\" title=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 3.\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\"><a class=\"sp-img-zoom\" href=\"https://kenh14cdn.com/2018/4/28/2a-1524893627462942180635.jpg\" rel=\"lightbox\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box; cursor:-webkit-zoom-in; color:transparent; display:block; height:245px; position:relative; width:326.656px\" title=\"\"><img alt=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2018/4/28/2a-1524893627462942180635.jpg\" style=\"border:0px; box-sizing:border-box; color:transparent; display:block; font:inherit; height:245px; margin:0px; max-width:100%; padding:0px; transition:all 0.25s ease-in-out 0s; vertical-align:baseline; width:326.667px\" title=\"Gợi ý 7 điểm ăn ngon giá hợp lý mà khi đi du lịch Hong Kong thì bạn không nên bỏ qua - Ảnh 3.\" /></a></span></span></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><span style=\"background-color:#ffffff\">Thậm ch&iacute; c&oacute; kh&aacute;ch h&agrave;ng c&ograve;n cho rằng m&oacute;n b&ograve; ở Kau Kee c&oacute; thể xem l&agrave; một trong những m&oacute;n l&agrave;m từ b&ograve; ngon nhất&nbsp;<a class=\"link-tag\" href=\"http://kenh14.vn/the-gioi.chn\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box\" target=\"_blank\" title=\" Thế Giới\">thế giới</a>. Thịt b&ograve;, g&acirc;n b&ograve; được nấu ch&iacute;n mềm, nước d&ugrave;ng th&igrave; c&oacute; độ ngon ngọt qu&aacute; chuẩn. V&agrave; khi đến với Kau Kee, bạn sẽ c&oacute; 2 sự lựa chọn tuyệt vời. Thứ nhất l&agrave; m&oacute;n m&igrave; nạm b&ograve; v&agrave; thứ 2 l&agrave; m&oacute;n c&agrave; ri nạm b&ograve; k&egrave;m g&acirc;n. Đ&acirc;y l&agrave; 2&nbsp;<a class=\"link-tag\" href=\"http://kenh14.vn/mon-an-ngon.html\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box\" target=\"_blank\" title=\"món ăn ngon\">m&oacute;n ăn</a>&nbsp;được đ&aacute;nh gi&aacute; cao nhất ở Kau Kee n&ecirc;n khi đến đ&acirc;y nhất định bạn phải nhớ thử qua.</span></span></span></p>\r\n\r\n<div class=\"VCSortableInPreviewMode active\" id=\"ObjectBoxContent_1524894105060\" style=\"-webkit-text-stroke-width:0px; border:2px solid #fecb2e; margin-bottom:0px; margin-left:22px; margin-right:22px; margin-top:0px; padding:10px 25px !important; text-align:justify !important; width:550px\">\r\n<div style=\"border:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; padding:0px\">\r\n<p style=\"margin-left:0px !important; margin-right:0px !important\"><span style=\"font-size:12pt\"><span style=\"font-family:SFD-Medium\"><span style=\"color:#222222\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><strong><a class=\"link-tag\" href=\"http://kenh14.vn/dia-diem-an-uong.html\" style=\"margin:0px; padding:0px; border:0px; font:inherit; vertical-align:baseline; text-decoration:none; box-sizing:border-box\" target=\"_blank\" title=\"địa điểm ăn uống\">Địa chỉ</a>:</strong>&nbsp;21 Gough Street, Central (中環歌賦街21號)</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0px !important; margin-right:0px !important\"><span style=\"font-size:12pt\"><span style=\"font-family:SFD-Medium\"><span style=\"color:#222222\"><span style=\"font-size:17px\"><span style=\"color:#222222\"><strong>Giờ mở cửa:&nbsp;</strong>